mapper.update:
update flight_table
set seat_status=#{seat_status}
where flight_id=#{flight_id}

update flight_table
set bc_remaining=bc_remaining-1
where flight_id=#{flight_id}

update flight_table
set ec_remaining=(ec_remaining-1)
where flight_id=#{flight_id}

update flight_table
set bc_remaining=bc_remaining+1
where flight_id=#{flight_id}

update flight_table
set ec_remaining=ec_remaining+1
where flight_id=#{flight_id}
